interface JQueryStatic {
  confirm(options?: any): JQuery;
  pulsate(options?: any): JQuery;
  datetimepicker(options?: any): JQuery;
  alert(message: any): JQuery;
  multiSelect(value?: any, options?: any): JQuery;
}

interface JQuery {
  confirm(options?: any): JQuery;
  pulsate(options?: any): JQuery;
  datetimepicker(options?: any): JQuery;
  multiSelect(value?: any, options?: any): JQuery;
}